import { useEffect, useState } from 'react';
import { tableAPI } from '../services/api';
import { useOrder } from '../context/OrderContext';
import { useNavigate } from 'react-router-dom';

export default function Tables() {
  const [tables, setTables] = useState([]);
  const { setTableBooking } = useOrder();
  const navigate = useNavigate();

  useEffect(() => {
    tableAPI.getAll()
      .then(res => setTables(res.data))
      .catch(() => alert('Lỗi tải danh sách bàn!'));
  }, []);

  const handleSelect = (table) => {
    setTableBooking({
      id: table.id,
      name: table.name,
      capacity: table.capacity
    });
    alert(`Đã chọn: ${table.name}`);
    navigate('/menu');
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-5 fw-bold text-primary">Chọn Bàn</h2>
      <div className="row g-4">
        {tables.length === 0 ? (
          <div className="col-12 text-center">
            <div className="alert alert-info">Không có bàn nào</div>
          </div>
        ) : (
          tables.map(table => (
            <div className="col-md-4" key={table.id}>
              <div
                className="card text-center p-4 shadow-sm cursor-pointer hover-shadow border-primary"
                onClick={() => handleSelect(table)}
              >
                <h5 className="fw-bold text-primary">{table.name}</h5>
                <p className="text-muted mb-2">Sức chứa: {table.capacity} người</p>
                <button className="btn btn-outline-primary btn-sm">
                  Chọn bàn này
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
