// src/pages/Admin/AdminOrders.jsx
import { useEffect, useState } from 'react';
import { orderAPI, tableAPI } from '../../services/api';

export default function AdminOrders() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    orderAPI.getAll().then(res => setOrders(res.data));
  }, []);

  const updateStatus = async (orderId, status, tableId) => {
    await orderAPI.updateStatus(orderId, status);
    if (status === 'COMPLETED') {
      const statusId = '1'; // AVAILABLE
      await tableAPI.updateStatus(tableId, statusId);
    }
    orderAPI.getAll().then(res => setOrders(res.data));
  };

  return (
    <div className="container py-4">
      <h2>Quản Lý Đơn Hàng</h2>
      <table className="table">
        <thead>
          <tr>
            <th>Bàn</th>
            <th>Khách</th>
            <th>Món</th>
            <th>Tổng</th>
            <th>Trạng thái</th>
            <th>Hành động</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td>{order.table.name}</td>
              <td>{order.user?.username || 'N/A'}</td>
              <td>{order.items.map(i => `${i.productName} x${i.quantity}`).join(', ')}</td>
              <td>{order.totalPrice}đ</td>
              <td>{order.status.name}</td>
              <td>
                {order.status.name === 'PENDING' && (
                  <button onClick={() => updateStatus(order.id, 'PREPARING', order.table.id)} className="btn btn-sm btn-warning">Chuẩn bị</button>
                )}
                {order.status.name === 'PREPARING' && (
                  <button onClick={() => updateStatus(order.id, 'COMPLETED', order.table.id)} className="btn btn-sm btn-success">Hoàn thành</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
