import { useEffect, useState } from 'react';
import { tableAPI } from '../services/api';
import { useOrder } from '../context/OrderContext';
import { useNavigate } from 'react-router-dom';

export default function Tables() {
  const [tables, setTables] = useState([]);
  const [selectedTableId, setSelectedTableId] = useState(null);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [guests, setGuests] = useState(1);
  const { setTableBooking } = useOrder();
  const navigate = useNavigate();

  useEffect(() => {
    tableAPI.getAvailable()
      .then(res => setTables(res.data))
      .catch(err => {
        console.error(err);
        alert('Lỗi tải danh sách bàn!');
      });
  }, []);

  const handleBook = async () => {
    if (!selectedTableId || !date || !time || guests < 1) {
      alert('Vui lòng nhập đầy đủ thông tin!');
      return;
    }

    const bookingData = {
      tableId: selectedTableId,
      date,
      time,
      guests
    };

    try {
      const res = await tableAPI.book(bookingData);
      setTableBooking(res.data); // { id, table: { name, capacity }, ... }
      alert(`Đặt bàn ${res.data.table.name} thành công!`);
      navigate('/menu');
    } catch (err) {
      alert('Lỗi đặt bàn: ' + (err.response?.data?.message || 'Không xác định'));
    }
  };

  // Hàm hiển thị trạng thái bàn
  const getStatusBadge = (statusName) => {
    switch (statusName) {
      case 'AVAILABLE':
        return <span className="badge bg-success">Còn trống</span>;
      case 'OCCUPIED':
        return <span className="badge bg-warning">Đã đặt</span>;
      case 'PAID':
        return <span className="badge bg-secondary">Đã thanh toán</span>;
      default:
        return <span className="badge bg-dark">Không xác định</span>;
    }
  };

  return (
    <div className="container py-5">
      <div className="text-center mb-5">
        <h2 className="fw-bold text-primary">Chọn Bàn Yêu Thích</h2>
        <p className="text-muted">Hệ thống sẽ tự kiểm tra bàn trống theo thời gian bạn chọn</p>
      </div>

      <div className="row g-4 mb-5">
        {tables.length === 0 ? (
          <div className="col-12 text-center">
            <div className="alert alert-info">Không có bàn nào trống vào thời điểm này</div>
          </div>
        ) : (
          tables.map(table => (
            <div className="col-md-4" key={table.id}>
              <div
                className={`card h-100 shadow-sm cursor-pointer transition-all ${
                  selectedTableId === table.id ? 'border-primary ring-2 ring-primary' : ''
                }`}
                onClick={() => setSelectedTableId(table.id)}
              >
                <div className="card-body text-center">
                  <h5 className="card-title fw-bold text-primary">{table.name}</h5>
                  <p className="card-text">
                    <strong>Sức chứa:</strong> {table.capacity} người
                  </p>
                  <div className="mt-2">
                    {getStatusBadge(table.status.name)}
                  </div>
                </div>
              </div>
            </div>
          ))
        )}
      </div>

      {/* Form đặt bàn */}
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow">
            <div className="card-header bg-primary text-white">
              <h5 className="mb-0">Xác nhận đặt bàn</h5>
            </div>
            <div className="card-body">
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label fw-bold">Ngày</label>
                  <input
                    type="date"
                    className="form-control"
                    value={date}
                    onChange={e => setDate(e.target.value)}
                    min={new Date().toISOString().split('T')[0]}
                    required
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label fw-bold">Giờ</label>
                  <input
                    type="time"
                    className="form-control"
                    value={time}
                    onChange={e => setTime(e.target.value)}
                    required
                  />
                </div>
                <div className="col-12">
                  <label className="form-label fw-bold">Số khách</label>
                  <input
                    type="number"
                    className="form-control"
                    value={guests}
                    onChange={e => setGuests(Math.max(1, parseInt(e.target.value) || 1))}
                    min="1"
                    max="20"
                    required
                  />
                </div>
                <div className="col-12">
                  <button
                    className="btn btn-success w-100 fw-bold"
                    onClick={handleBook}
                    disabled={!selectedTableId}
                  >
                    Xác Nhận Đặt Bàn
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
