import { createContext, useContext, useState } from 'react';
import { orderAPI } from '../services/api';
import { useNavigate } from 'react-router-dom';

const OrderContext = createContext();
export const useOrder = () => useContext(OrderContext);

export const OrderProvider = ({ children }) => {
  const [orderItems, setOrderItems] = useState([]);
  const [tableBooking, setTableBooking] = useState(null); // { id, name, capacity }
  const navigate = useNavigate();

  const addItem = (product) => {
    if (!tableBooking) {
      alert('Vui lòng chọn bàn trước!');
      return;
    }
    setOrderItems(prev => {
      const exist = prev.find(item => item.id === product.id);
      if (exist) {
        return prev.map(item =>
          item.id === product.id ? { ...item, qty: item.qty + 1 } : item
        );
      }
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeItem = (id) => {
    setOrderItems(prev => prev.filter(item => item.id !== id));
  };

  const updateQty = (id, qty) => {
    if (qty < 1) return removeItem(id);
    setOrderItems(prev => prev.map(item => item.id === id ? { ...item, qty } : item));
  };

  const totalPrice = orderItems.reduce((sum, item) => sum + item.price * item.qty, 0);

  const createOrder = async () => {
    if (!tableBooking) {
      alert('Chưa chọn bàn!');
      return;
    }
    if (orderItems.length === 0) {
      alert('Chưa chọn món nào!');
      return;
    }

    const items = orderItems.map(item => ({
      productId: item.id,
      productName: item.name,
      quantity: item.qty,
      price: item.price
    }));

    const orderData = {
      tableId: tableBooking.id,
      items
    };

    try {
      await orderAPI.create(orderData);
      alert('Đặt món thành công!');
      setOrderItems([]);
      setTableBooking(null);
      navigate('/order-success');
    } catch (err) {
      alert('Lỗi: ' + (err.response?.data?.message || 'Không xác định'));
    }
  };

  return (
    <OrderContext.Provider value={{
      orderItems, addItem, removeItem, updateQty, totalPrice,
      tableBooking, setTableBooking, createOrder
    }}>
      {children}
    </OrderContext.Provider>
  );
};
