import { createContext, useContext, useState } from 'react';
import { orderAPI } from '../services/api';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);
  const [tableBooking, setTableBooking] = useState(null); // {id, ...}

  const addToCart = (product) => {
    if (!tableBooking) {
      alert('Vui lòng đặt bàn trước khi đặt món!');
      return;
    }
    setCart((prev) => {
      const exist = prev.find((item) => item.id === product.id);
      if (exist) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, qty: item.qty + 1 } : item
        );
      }
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  const updateQty = (id, qty) => {
    if (qty < 1) return removeFromCart(id);
    setCart((prev) =>
      prev.map((item) => (item.id === id ? { ...item, qty } : item))
    );
  };

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const submitOrder = async () => {
    if (cart.length === 0) return alert('Giỏ hàng trống!');
    if (!tableBooking) return alert('Chưa đặt bàn!');

    const orderData = {
      items: cart.map((item) => ({
        productId: item.id,
        productName: item.name,
        quantity: item.qty,
        price: item.price,
      })),
      tableId: tableBooking.id, // Backend sẽ map với table
      // Backend tự thêm user, status, createdAt, totalPrice
    };

    try {
      await orderAPI.create(orderData);
      alert('Đặt hàng thành công!');
      setCart([]);
      setTableBooking(null);
    } catch (err) {
      alert('Lỗi: ' + (err.response?.data?.message || 'Không xác định'));
    }
  };

  return (
    <CartContext.Provider
      value={{
        cart,
        addToCart,
        removeFromCart,
        updateQty,
        total,
        tableBooking,
        setTableBooking,
        submitOrder,
      }}
    >
      {children}
    </CartContext.Provider>
  );
};
