import { useEffect, useState } from 'react';
import { tableAPI, tableStatusAPI } from '../services/api';
import TableCard from '../components/TableCard';

export default function Tables() {
  const [tables, setTables] = useState([]);
  const [bookedStatus, setBookedStatus] = useState(null);

  useEffect(() => {
    // Load bàn + trạng thái BOOKED
    const loadData = async () => {
      try {
        const tableRes = await tableAPI.getAll();
        const statusRes = await tableStatusAPI.getAll();
        setTables(tableRes.data);
        const booked = statusRes.find(s => s.name === 'BOOKED' || s.name === 'OCCUPIED');
        setBookedStatus(booked);
      } catch {
        alert('Lỗi tải dữ liệu!');
      }
    };
    loadData();
  }, []);

  const handleSelectTable = async (table) => {
    if (!bookedStatus) return alert('Không tìm thấy trạng thái BOOKED!');

    try {
      // Update trạng thái bàn thành BOOKED
      await tableAPI.update(table.id, { ...table, status: bookedStatus });
      // Cập nhật lại danh sách bàn
      const updatedTables = tables.map(t => t.id === table.id ? { ...t, status: bookedStatus } : t);
      setTables(updatedTables);
    } catch {
      alert('Đặt bàn thất bại!');
    }
  };

  return (
    <div className="container py-5">
      <h1 className="text-center mb-5 text-primary">Chọn Bàn Trống</h1>
      <div className="row">
        {tables.length === 0 ? (
          <div className="col-12 text-center text-muted">Không có bàn nào.</div>
        ) : (
          tables.map(table => (
            <TableCard
              key={table.id}
              table={table}
              isAdmin={false}
              onSelect={handleSelectTable} // callback update trạng thái
            />
          ))
        )}
      </div>
    </div>
  );
}
