import { useEffect, useState } from 'react';
import { tableAPI } from '../services/api';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';

export default function Tables() {
  const [tables, setTables] = useState([]);
  const [selectedTableId, setSelectedTableId] = useState(null);
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [guests, setGuests] = useState(1);
  const { setTableBooking } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    tableAPI.getAvailable()
      .then(res => setTables(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleBook = async () => {
    if (!selectedTableId || !date || !time || guests < 1) {
      alert('Vui lòng chọn đầy đủ thông tin!');
      return;
    }

    const bookingData = {
      tableId: selectedTableId,
      date,
      time,
      guests
    };

    try {
      const res = await tableAPI.book(bookingData);
      setTableBooking(res.data); // Giả định res.data có id
      alert('Đặt bàn thành công!');
      navigate('/menu');
    } catch (err) {
      alert('Lỗi đặt bàn: ' + (err.response?.data?.message || ''));
    }
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4">Đặt Bàn</h2>
      <div className="row">
        {tables.map(table => (
          <div className="col-md-3 mb-3" key={table.id}>
            <div 
              className={`card text-center p-3 cursor-pointer ${selectedTableId === table.id ? 'border-primary bg-light' : ''}`}
              onClick={() => setSelectedTableId(table.id)}
            >
              <h5>Bàn {table.number}</h5>
              <p>Sức chứa: {table.capacity} người</p> {/* Giả định field, thay bằng thật */}
              <p>Trạng thái: {table.available ? 'Có sẵn' : 'Bận'}</p>
            </div>
          </div>
        ))}
      </div>
      <div className="row justify-content-center mt-4">
        <div className="col-md-6">
          <div className="card p-4">
            <div className="mb-3">
              <label className="form-label">Ngày</label>
              <input type="date" className="form-control" value={date} onChange={e => setDate(e.target.value)} />
            </div>
            <div className="mb-3">
              <label className="form-label">Giờ</label>
              <input type="time" className="form-control" value={time} onChange={e => setTime(e.target.value)} />
            </div>
            <div className="mb-3">
              <label className="form-label">Số khách</label>
              <input type="number" min="1" className="form-control" value={guests} onChange={e => setGuests(parseInt(e.target.value))} />
            </div>
            <button className="btn btn-primary w-100" onClick={handleBook}>Xác nhận đặt bàn</button>
          </div>
        </div>
      </div>
    </div>
  );
}
