import { createContext, useContext, useState } from 'react';
import { orderAPI } from '../services/api';

const OrderContext = createContext();

export const useOrder = () => useContext(OrderContext);

export const OrderProvider = ({ children }) => {
  const [orderItems, setOrderItems] = useState([]);
  const [tableBooking, setTableBooking] = useState(null); // { id, ... }

  const addItem = (product) => {
    if (!tableBooking) {
      alert('Vui lòng đặt bàn trước khi thêm món!');
      return;
    }

    setOrderItems((prev) => {
      const exist = prev.find((item) => item.id === product.id);
      if (exist) {
        return prev.map((item) =>
          item.id === product.id ? { ...item, qty: item.qty + 1 } : item
        );
      }
      return [...prev, { ...product, qty: 1 }];
    });
  };

  const removeItem = (id) => {
    setOrderItems((prev) => prev.filter((item) => item.id !== id));
  };

  const updateQty = (id, qty) => {
    if (qty < 1) return removeItem(id);
    setOrderItems((prev) =>
      prev.map((item) => (item.id === id ? { ...item, qty } : item))
    );
  };

  const totalPrice = orderItems.reduce(
    (sum, item) => sum + item.price * item.qty,
    0
  );

  const createOrder = async () => {
    if (orderItems.length === 0) {
      alert('Chưa có món nào trong đơn!');
      return;
    }
    if (!tableBooking) {
      alert('Chưa đặt bàn!');
      return;
    }

    const items = orderItems.map((item) => ({
      productId: item.id,
      productName: item.name,
      quantity: item.qty,
      price: item.price,
    }));

    const orderData = {
      items,
      tableId: tableBooking.id,
      // Backend sẽ tự thêm: user, status, createdAt, totalPrice
    };

    try {
      await orderAPI.create(orderData);
      alert('Tạo đơn hàng thành công!');
      setOrderItems([]);
      setTableBooking(null);
    } catch (err) {
      const msg = err.response?.data?.message || 'Lỗi không xác định';
      alert('Tạo đơn thất bại: ' + msg);
    }
  };

  return (
    <OrderContext.Provider
      value={{
        orderItems,
        addItem,
        removeItem,
        updateQty,
        totalPrice,
        tableBooking,
        setTableBooking,
        createOrder,
      }}
    >
      {children}
    </OrderContext.Provider>
  );
};
