// src/pages/Admin/AdminReports.jsx
import { useEffect, useState } from 'react';
import { orderAPI } from '../../services/api';

export default function AdminReports() {
  const [report, setReport] = useState({ totalOrders: 0, totalRevenue: 0 });

  useEffect(() => {
    orderAPI.getAll().then(res => {
      const completed = res.data.filter(o => o.status.name === 'COMPLETED');
      const total = completed.reduce((sum, o) => sum + o.totalPrice, 0);
      setReport({ totalOrders: completed.length, totalRevenue: total });
    });
  }, []);

  return (
    <div className="container py-4">
      <h2>Báo Cáo Doanh Thu Hôm Nay</h2>
      <div className="row">
        <div className="col-md-6">
          <div className="card p-4 text-center">
            <h4>{report.totalOrders}</h4>
            <p>Tổng đơn hàng</p>
          </div>
        </div>
        <div className="col-md-6">
          <div className="card p-4 text-center">
            <h4>{report.totalRevenue}đ</h4>
            <p>Tổng doanh thu</p>
          </div>
        </div>
      </div>
    </div>
  );
}
