// src/components/TableCard.jsx
import { useOrder } from '../context/OrderContext';
import { useNavigate } from 'react-router-dom';

export default function TableCard({ table }) {
  const { setSelectedTable } = useOrder();
  const navigate = useNavigate();

  const handleSelect = () => {
    if (table.status?.name !== 'AVAILABLE') return;
    setSelectedTable(table);
    navigate('/menu');
  };

  return (
    <div className="col-md-4 mb-3">
      <div className={`card text-center p-3 h-100 ${table.status?.name !== 'AVAILABLE' ? 'bg-secondary text-white' : ''}`}>
        <h5 className="card-title">{table.name}</h5>
        <p className="card-text">Sức chứa: {table.capacity} người</p>
        <p className="card-text">
          <strong>Trạng thái:</strong> {table.status?.name || 'N/A'}
        </p>
        <button
          onClick={handleSelect}
          disabled={table.status?.name !== 'AVAILABLE'}
          className={`btn ${table.status?.name === 'AVAILABLE' ? 'btn-success' : 'btn-secondary'} w-100`}
        >
          {table.status?.name === 'AVAILABLE' ? 'Chọn bàn' : 'Đã đặt'}
        </button>
      </div>
    </div>
  );
}
