import { useOrder } from '../context/OrderContext';
import { Link, useNavigate } from 'react-router-dom';

export default function OrderPage() {
  const {
    orderItems,
    removeItem,
    updateQty,
    totalPrice,
    tableBooking,
    createOrder
  } = useOrder();
  
  const navigate = useNavigate(); // DÙNG Ở ĐÂY, TRONG <Router>

  const handleCreateOrder = async () => {
    const success = await createOrder(() => {
      navigate('/order-success'); // CHUYỂN TRANG SAU KHI THÀNH CÔNG
    });
  };

  if (!tableBooking) {
    return (
      <div className="container py-5 text-center">
        <div className="alert alert-danger">
          Vui lòng <Link to="/tables">chọn bàn</Link> trước!
        </div>
      </div>
    );
  }

  if (orderItems.length === 0) {
    return (
      <div className="container py-5 text-center">
        <h3>Chưa có món nào</h3>
        <Link to="/menu" className="btn btn-primary mt-3">
          Quay lại thực đơn
        </Link>
      </div>
    );
  }

  return (
    <div className="container py-5">
      <h2 className="mb-4">Đơn Hàng Của Bạn</h2>

      <div className="card mb-4">
        <div className="card-body">
          <h5>Bàn: <strong>{tableBooking.name}</strong> ({tableBooking.capacity} người)</h5>
        </div>
      </div>

      <div className="table-responsive">
        <table className="table table-striped align-middle">
          <thead className="table-light">
            <tr>
              <th>Món</th>
              <th>Giá</th>
              <th>Số lượng</th>
              <th>Tổng</th>
              <th>Hành động</th>
            </tr>
          </thead>
          <tbody>
            {orderItems.map(item => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.price.toLocaleString('vi-VN')}đ</td>
                <td>
                  <div className="d-flex align-items-center">
                    <button className="btn btn-sm btn-outline-secondary" onClick={() => updateQty(item.id, item.qty - 1)}>-</button>
                    <span className="mx-3">{item.qty}</span>
                    <button className="btn btn-sm btn-outline-secondary" onClick={() => updateQty(item.id, item.qty + 1)}>+</button>
                  </div>
                </td>
                <td>{(item.price * item.qty).toLocaleString('vi-VN')}đ</td>
                <td>
                  <button className="btn btn-sm btn-danger" onClick={() => removeItem(item.id)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="text-end mt-4">
        <h4 className="text-primary">
          Tổng tiền: <strong>{totalPrice.toLocaleString('vi-VN')}đ</strong>
        </h4>
        <button className="btn btn-success btn-lg" onClick={handleCreateOrder}>
          Xác Nhận Đặt Món
        </button>
      </div>
    </div>
  );
}
