import { useEffect, useState } from 'react';
import { productAPI } from '../services/api';
import { useOrder } from '../context/OrderContext';

export default function Menu() {
  const [products, setProducts] = useState([]);
  const { addItem, tableBooking } = useOrder();

  useEffect(() => {
    productAPI.getAll()
      .then(res => setProducts(res.data))
      .catch(err => console.error(err));
  }, []);

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4">Thực Đơn</h2>
      {!tableBooking && (
        <div className="alert alert-warning text-center">
          Bạn cần đặt bàn trước khi đặt món! <a href="/tables" className="alert-link">Đặt bàn ngay</a>
        </div>
      )}
      <div className="row">
        {products.map(product => (
          <div className="col-md-4 mb-4" key={product.id}>
            <div className="card h-100">
              <img src={product.image || 'placeholder.jpg'} className="card-img-top" alt={product.name} style={{height: '200px', objectFit: 'cover'}} />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text">{product.description}</p>
                <p className="card-text fw-bold">{product.price.toLocaleString('vi-VN')} đ</p>
                <button 
                  className="btn btn-primary mt-auto" 
                  onClick={() => addToCart(product)}
                  disabled={!tableBooking}
                >
                  Thêm món
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
