import axios from 'axios';

const API_URL = 'http://localhost:8080/api'; // Thay bằng URL backend Spring Boot

axios.defaults.baseURL = API_URL;

axios.interceptors.request.use(config => {
  const token = localStorage.getItem('token');
  if (token) config.headers.Authorization = `Bearer ${token}`;
  return config;
});

export const authAPI = {
  login: (data) => axios.post('/auth/login', {
    username: data.username,
    password: data.password
  }),
  register: (data) => axios.post('/auth/register', {
    username: data.username,
    email: data.email,        
    password: data.password
  }),
};

export const tableAPI = {
  getAll: () => axios.get('/tables'), // Lấy danh sách bàn
};

export const productAPI = {
  getAll: () => axios.get('/products'),
};

export const orderAPI = {
  create: (data) => axios.post('/orders', data), // { tableId, items }
};
