import { useEffect, useState } from 'react';
import { productAPI } from '../services/api';
import { useOrder } from '../context/OrderContext';
import { Link } from 'react-router-dom';

export default function Menu() {
  const [products, setProducts] = useState([]);
  const { addItem, tableBooking } = useOrder();

  useEffect(() => {
    productAPI.getAll()
      .then(res => setProducts(res.data))
      .catch(() => alert('Lỗi tải thực đơn!'));
  }, []);

  return (
    <div className="container py-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2 className="fw-bold">Thực Đơn</h2>
        {tableBooking && (
          <div className="alert alert-info py-2 px-3 mb-0">
            Bàn: <strong>{tableBooking.name}</strong> ({tableBooking.capacity} người)
          </div>
        )}
      </div>

      {!tableBooking && (
        <div className="alert alert-warning text-center">
          Vui lòng <Link to="/tables" className="alert-link">chọn bàn</Link> trước khi đặt món!
        </div>
      )}

      <div className="row g-4">
        {products.map(product => (
          <div className="col-md-4" key={product.id}>
            <div className="card h-100 shadow-sm">
              <img
                src={product.image || '/placeholder.jpg'}
                className="card-img-top"
                alt={product.name}
                style={{ height: '200px', objectFit: 'cover' }}
              />
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{product.name}</h5>
                <p className="card-text text-muted flex-grow-1">{product.description}</p>
                <div className="d-flex justify-content-between align-items-center mt-3">
                  <span className="fw-bold text-primary">
                    {product.price.toLocaleString('vi-VN')}đ
                  </span>
                  <button
                    className="btn btn-primary"
                    onClick={() => addItem(product)}
                    disabled={!tableBooking}
                  >
                    Thêm món
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {tableBooking && (
        <div className="text-center mt-5">
          <Link to="/order" className="btn btn-success btn-lg">
            Xem đơn hàng ({orderItems.length} món)
          </Link>
        </div>
      )}
    </div>
  );
}
